	const toastId = React.useRef(null);
	const [oldCode, setOldCode] = useState("");
	const [newCode, setNewCode] = useState("");
	const Toastobjects = {
		pauseOnFocusLoss: false,
		draggable: false,
		pauseOnHover: false,
		autoClose: 2000,
	};

	const dispatch = useDispatch();
	console.log(useSelector((state) => state));
	const userDetails = useSelector((state) => state.userDetails);
	const { loadingUser, error, user } = userDetails;

	const userUpdateProfile = useSelector((state) => state.userUpdateProfile);
	const { loadingUpdate, successUpdate, errorUpdate } = userUpdateProfile;
	useEffect(() => {
		dispatch(getUserDetails("profile"));
	}, [dispatch, userUpdateProfile]);
	useEffect(() => {
		if (successUpdate) {
			if (!toast.isActive(toastId.current)) {
				toastId.current = toast.success("Success", Toastobjects);
			}
		} else if (errorUpdate) {
			if (!toast.isActive(toastId.current)) {
				toastId.current = toast.error(errorUpdate, Toastobjects);
			}
		}
	}, [userUpdateProfile]);
	const submitHandler = (e) => {
		e.preventDefault();
		// Password match
		console.log("hi");
		if (
			user.confirmationCode != "Not Set" &&
			oldCode !== user.confirmationCode
		) {
			if (!toast.isActive(toastId.current)) {
				toastId.current = toast.error(
					"Wrong old Confirmation Code",
					Toastobjects
				);
			}
		} else {
			dispatch(updateUserProfile({ id: user._id, confirmationCode: newCode }));
		}
	};
